mapper.select:
select vir_acct_type,vir_acct_desc,vir_acct_sort,is_in,is_out,is_total_limit,total_limit,is_balance_limit,
balance_type,balance_value from va_virtual_acct_type

     and vir_acct_type = #{vir_acct_type}

select count(1) from va_merch_info

      and status = #{status} 
     and tel_no = #{tel_no}
     and merch_id = #{merch_id}

select count(1) from va_plat_info

     and status = #{status}
     and plat_id = #{plat_id} 
     and pay_merch_id = #{pay_merch_id}

select
  acct_id,cust_id,acct_type,acct_no,acct_status,acct_category,acct_name,
  is_this_bank,acct_br_no,acct_br_name,currency
from va_cust_acct_info

     and acct_status = #{acct_status} 
     and acct_type = #{acct_type}
     and cust_id = #{cust_id}

select count(1) from va_cust_info

     and status = #{status} 
     and tel_no = #{tel_no}
     and cust_id = #{cust_id}

select
    acct_id,merch_id,acct_type,acct_no,acct_status,acct_category,acct_name,
    is_this_bank,acct_br_no,acct_br_name,currency
from va_merch_acct_info

     and acct_category = #{acct_category} 
     and acct_status = #{acct_status} 
     and acct_type = #{acct_type}
     and merch_id = #{merch_id}

select actural_bal,usable_bal,recharge_bal from va_merch_virtual_acct_bal

     and vir_acct_type = #{vir_acct_type} 
     and merch_id = #{merch_id} 
     and merch_virid = #{merch_virid} 

for update

select actural_bal,usable_bal,recharge_bal from va_cust_virtual_acct_bal

     and vir_acct_type = #{vir_acct_type} 
     and cust_id = #{cust_id} 
     and cust_virid = #{cust_virid} 

for update

select
    cust_recharge_seq,order_no,pub_seq_no,
    plat_id,cust_id,
    vir_acct_id,tran_type,tran_amt,
    tran_status,tran_date
from va_cust_recharge_seq

     and vir_acct_id = #{plat_id} 
     and tran_status = #{tran_status} 
     and cust_id = #{cust_id} 
     and vir_acct_id = #{vir_acct_id} 
     and tran_date = str_to_date(#{tran_date},'%Y%m%d')

select
  coalesce(sum(tran_amt),0) sum_amt,count(1) sum_cnt
from va_cust_recharge_seq

     and plat_id = #{plat_id} 
     and payee_id = #{payee_id} 
     and payee_virid = #{payee_virid} 
     and tran_date = str_to_date(#{tran_date},'%Y%m%d') 
     and order_no = #{order_no}

select
  coalesce(sum(tran_amt),0) sum_amt,count(1) sum_cnt
from va_merch_recharge_seq

     and plat_id = #{plat_id} 
     and payee_id = #{payee_id} 
     and payee_virid = #{payee_virid} 
     and tran_date = str_to_date(#{tran_date},'%Y%m%d') 
     and order_no = #{order_no}

select order_acct_seq_no,order_no,order_no_old,
cm_seq_no, plat_id,buyer_id,buyer_virid,
buyer_sort, seller_id, seller_virid,seller_sort,
pay_type,tran_amt,usable_bal,recharge_bal,
tran_type,tran_status,tran_date,tran_time
from va_vir_acct_seq

    and tran_type=#{tran_type}
    and tran_status=#{tran_status}
    and plat_id=#{plat_id}
    and buyer_id=#{buyer_id}
    and seller_virid=#{seller_virid}
    and tran_date=str_to_date(#{tran_date},'%Y%m%d')
    and cm_seq_no=#{cm_seq_no}
     and order_no_old=#{order_no_old}
     and order_no=#{order_no}
    and order_acct_seq_no=#{order_acct_seq_no}

select column_name,data_type
from information_schema.columns
where table_schema = 'vas' and table_name = #{table_name}
